mapper.sql:
    select top 100 percent sewage.name as sewagename, devicedoc.devicename as devicename, e.id,e.sewageid,e.deviceid,e.time,e.consumetime,e.repairreason,e.repaircontent,e.consumematerial,e.repairman,e.completetime 
	from equiprepairrecord as e,devicedoc,sewage where e.deviceid = devicedoc.id and e.sewageid = sewage.sewageID

       and e.sewageid = #{sewageid}
