mapper.sql:
select temp1.sewageID,sewage.operationnum,sewage.name from ( (select sewageid from (select temp1.sewageID,temp1.testingtime,sewage.areaID from sewage , (select sewageid,max(testingtime) as testingtime from run_data group by sewageid ) as temp1 where sewage.sewageID = temp1.sewageId and CONVERT(varchar(12),testingtime,111) != CONVERT(varchar(12),getdate() ,111)
and areaid = #{areaid}
) as tmp) union (select sewage.sewageid from (select distinct detection_data.sewageid from detection_data) tmp1 right join sewage on sewage.sewageid = tmp1.sewageid where tmp1.sewageid is null
and sewage.areaid = #{areaid}
) ) as temp1,sewage where temp1.sewageID = sewage.sewageID
